mapper.select:
SELECT 

FROM s_goods a

WHERE a.id = #{id}

SELECT
FROM s_goods a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.class_id = #{gClass.id}
AND a.gener_id = #{gener.id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND sgc.name LIKE '%'||#{gClass.name}||'%' '%'+#{gClass.name}+'%' concat('%',#{gClass.name},'%')
AND sg.name LIKE '%'||#{gener.name}||'%' '%'+#{gener.name}+'%' concat('%',#{gener.name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.sort DESC

SELECT 

FROM s_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.sort DESC

mapper.update:
UPDATE s_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
