SELECT

FROM s_member a

WHERE a.login_key = #{loginKey}

SELECT
FROM s_member a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.login_name LIKE '%'||#{loginName}||'%' '%'+#{loginName}+'%' concat('%',#{loginName},'%')
AND a.login_key = #{loginKey}
AND a.mobile = #{mobile}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM s_member a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

