mapper.select:
SELECT 

FROM s_member_relation a

WHERE a.id = #{id}

SELECT
FROM s_member_relation a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.old_member_id = #{oldMember.id}
AND a.old_member_id LIKE '%'||#{oldMember.name}||'%' '%'+#{oldMember.name}+'%' concat('%',#{oldMember.name},'%')
AND a.new_member_id = #{newMember.id}
AND a.new_member_id LIKE '%'||#{newMember.name}||'%' '%'+#{newMember.name}+'%' concat('%',#{newMember.name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM s_member_relation a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_member_relation SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
