mapper.select:
SELECT 

FROM s_order a

WHERE a.id = #{id}

SELECT 

FROM s_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.order_no = #{orderNo}


		AND a.ddbs = #{ddbs}


		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
