mapper.select:
SELECT 

FROM s_proportion_commission a

WHERE a.id = #{id}

SELECT 

FROM s_proportion_commission a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.commission = #{commission}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_proportion_commission a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_proportion_commission SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
