mapper.select:
SELECT 

FROM s_spec_class a

WHERE a.id = #{id}

SELECT
FROM s_spec_class a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM s_spec_class a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_spec_class SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
