mapper.select:
SELECT 

FROM s_with_draw a

WHERE a.id = #{id}

SELECT 

FROM s_with_draw a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_by = #{createBy.id}


		AND a.state = #{state}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM s_with_draw a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE s_with_draw SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
